mapper.select:
SELECT DISTINCT cinema.id, cinema.name, cinema.description, cinema.home_page, cinema.tel, cinema.max_select_seat_count, cinema.full_address, cinema.latitude, cinema.longitude, cinema.postal_code, brand.id as brand_id, brand.name as brand_name, ( SELECT COUNT(theater_hall.cinema_id) FROM theater_hall WHERE theater_hall.cinema_id = cinema.id AND theater_hall.deleted = 0 ) AS theater_count,
( 6371000 * acos( cos(radians(#{query.latitude})) * cos(radians(cinema.latitude)) * cos(radians(cinema.longitude) - radians(#{query.longitude})) + sin(radians(#{query.latitude})) * sin(radians(cinema.latitude)) ) ) AS distance
NULL AS distance
FROM cinema LEFT JOIN brand ON cinema.brand_id = brand.id
LEFT JOIN cinema_spec_spec ON cinema.id = cinema_spec_spec.cinema_id
cinema.deleted = 0
AND cinema.name LIKE concat('%', #{query.name}, '%')
AND cinema.id = #{query.id}
AND cinema.region_id = #{query.regionId}
AND cinema.prefecture_id = #{query.prefectureId}
AND cinema.city_id = #{query.cityId}
AND cinema.brand_id = #{query.brandId}
AND cinema_spec_spec.spec_id = #{query.specId} AND cinema_spec_spec.deleted = 0
AND cinema.latitude IS NOT NULL AND cinema.longitude IS NOT NULL AND ( 6371000 * acos( cos(radians(#{query.latitude})) * cos(radians(cinema.latitude)) * cos(radians(cinema.longitude) - radians(#{query.longitude})) + sin(radians(#{query.latitude})) * sin(radians(cinema.latitude)) ) ) <= #{query.radius}
ORDER BY distance ASC
