mapper.select:
SELECT movie.*, level.name AS level_name FROM movie LEFT JOIN level ON movie.level_id = level.id
movie.deleted = 0
AND movie.id = #{query.id}
AND movie.name LIKE concat('%', #{query.name}, '%')
AND movie.status = #{query.status}
ORDER BY movie.update_time DESC

SELECT id, movie_id AS movieId, code, date FROM hello_movie WHERE movie_id IN
#{movieId}
AND deleted = 0

SELECT DISTINCT cinema.id AS cinema_id, cinema.name AS cinema_name, cinema.full_address AS cinema_address, cinema.tel AS cinema_tel, cinema.latitude AS cinema_latitude, cinema.longitude AS cinema_longitude, movie_show_time.id AS id, movie_show_time.start_time AS start_time, movie_show_time.end_time AS end_time, theater_hall.id AS theater_hall_id, theater_hall.name AS theater_hall_name, cinema_spec.name AS spec_name, movie_show_time.subtitle_id, movie_show_time.show_time_tag_id, movie_show_time.movie_version_id, movie_version.version_code, ( SELECT COUNT(seat.id) FROM seat WHERE seat.theater_hall_id = theater_hall.id AND seat.deleted = 0 AND seat.show = TRUE ) AS total_seats, ( SELECT COUNT(select_seat.id) FROM select_seat LEFT JOIN movie_order ON movie_order.id = select_seat.movie_order_id WHERE select_seat.movie_show_time_id = movie_show_time.id AND select_seat.deleted = 0 AND movie_order.order_state = 2 ) AS selected_seats,
( 6371000 * acos( cos(radians(#{query.latitude})) * cos(radians(cinema.latitude)) * cos(radians(cinema.longitude) - radians(#{query.longitude})) + sin(radians(#{query.latitude})) * sin(radians(cinema.latitude)) ) ) AS distance
NULL AS distance
FROM movie_show_time LEFT JOIN cinema ON movie_show_time.cinema_id = cinema."id" LEFT JOIN theater_hall ON movie_show_time.theater_hall_id = theater_hall.id LEFT JOIN cinema_spec ON movie_show_time.spec_id = cinema_spec.id LEFT JOIN movie_version ON movie_version.id = movie_show_time.movie_version_id AND movie_version.deleted = 0 WHERE movie_show_time.deleted = 0 AND movie_show_time.status = #{showTimeState} AND movie_show_time.movie_id = #{query.movieId} AND movie_show_time.start_time::timestamp >= now() AND cinema.deleted = 0
AND cinema.region_id = #{query.regionId}
AND cinema.prefecture_id = #{query.prefectureId}
AND cinema.city_id = #{query.cityId}
AND #{query.subtitleId} = ANY(movie_show_time.subtitle_id)
AND movie_show_time.spec_id IN
#{id}
AND #{query.showTimeTagId} = ANY(movie_show_time.show_time_tag_id)
AND movie_version.version_code = #{query.versionCode}
AND ( cinema.name LIKE concat('%', #{query.keyword}, '%') OR cinema.full_address LIKE concat('%', #{query.keyword}, '%') )
AND TO_CHAR(movie_show_time.start_time::timestamp, 'HH24:MI') >= #{query.startTimeFrom}
AND TO_CHAR(movie_show_time.start_time::timestamp, 'HH24:MI') <= #{query.startTimeTo}
AND cinema.latitude IS NOT NULL AND cinema.longitude IS NOT NULL AND ( 6371000 * acos( cos(radians(#{query.latitude})) * cos(radians(cinema.latitude)) * cos(radians(cinema.longitude) - radians(#{query.longitude})) + sin(radians(#{query.latitude})) * sin(radians(cinema.latitude)) ) ) <= #{query.radius}
ORDER BY distance ASC
