mapper.select:
SELECT movie_order.id, movie_order.order_total, movie_order.order_state, movie_order.pay_state, movie_order.pay_total, movie_order.pay_time, movie_order.pay_number, movie_order.create_time AS order_time, movie_show_time.start_time, movie_show_time.end_time, movie.name as movie_name, movie.cover as movie_poster, cinema.name as cinema_name, theater_hall.name as theater_hall_name, cinema_spec.name as theater_hall_spec_name, select_seat.x as seat_x, select_seat.y as seat_y, seat.seat_name, -- 查询 seat 表中的 seat_name movie_ticket_type.name as movie_ticket_type_name, payment_methods.name as pay_method FROM movie_order LEFT JOIN movie_show_time ON movie_order.movie_show_time_id = movie_show_time.id LEFT JOIN movie ON movie_show_time.movie_id = movie.id LEFT JOIN cinema ON movie_show_time.cinema_id = cinema.id LEFT JOIN theater_hall ON movie_show_time.theater_hall_id = theater_hall.id LEFT JOIN cinema_spec ON theater_hall.cinema_spec_id = cinema_spec.id LEFT JOIN select_seat ON select_seat.movie_order_id = movie_order.id LEFT JOIN seat ON seat.x_axis = select_seat.x AND seat.y_axis = select_seat.y LEFT JOIN movie_ticket_type ON select_seat.movie_ticket_type_id = movie_ticket_type.id LEFT JOIN payment_methods ON movie_order.pay_method_id = payment_methods.id
movie_order.deleted = 0
AND movie.id = #{query.movieId}
AND cinema.id = #{query.cinemaId}
AND theater_hall.id = #{query.theaterHallId}
AND movie_order.id = #{query.id}
AND movie_order.order_state = #{query.orderState}
AND movie_order.pay_state = #{query.payState}
AND movie_order.create_time >= TO_TIMESTAMP(#{query.orderStartTime}, 'YYYY-MM-DD HH24:MI:SS')
AND movie_order.create_time <= TO_TIMESTAMP(#{query.orderEndTime}, 'YYYY-MM-DD HH24:MI:SS')
