mapper.select:
SELECT movie_show_time.id, movie_show_time.start_time, movie_show_time.end_time, movie_show_time.status, movie_show_time.open, movie.id AS movie_id, movie.name AS movie_name, movie.cover AS movie_cover, cinema.id AS cinema_id, cinema.name AS cinema_name, theater_hall.id AS theater_hall_id, theater_hall.name AS theater_hall_name, language.id as subtitle_id, language.name as subtitle_name, movie_show_time_tag.id as show_time_tag_id, movie_show_time_tag.name as show_time_tag_name, ( SELECT COUNT(seat.show) FROM seat WHERE seat.deleted = 0 AND seat.theater_hall_id = theater_hall.id AND seat.show = TRUE ) AS seat_count, ( SELECT COUNT(*) FROM select_seat LEFT JOIN movie_order ON movie_order.id = select_seat.movie_order_id WHERE select_seat.movie_show_time_id = movie_show_time.id AND select_seat.movie_order_id IS NOT NULL AND select_seat.deleted = 0 AND movie_order.order_state = #{orderState} ) AS selected_seat_count, cinema_spec.id AS spec_id, cinema_spec.name AS spec_name, cinema_spec.description AS spec_description, cs2.name as theater_hall_spec FROM movie_show_time LEFT JOIN language ON movie_show_time.subtitle_id = language.id LEFT JOIN movie_show_time_tag ON movie_show_time.show_time_tag_id = movie_show_time_tag.id LEFT JOIN movie ON movie.id = movie_show_time.movie_id AND movie.deleted = 0 LEFT JOIN movie_spec ON movie.id = movie_spec.movie_id AND movie_spec.deleted = 0 LEFT JOIN cinema_spec ON movie_spec.spec_id = cinema_spec.id AND cinema_spec.deleted = 0 LEFT JOIN cinema ON cinema.id = movie_show_time.cinema_id AND cinema.deleted = 0 LEFT JOIN theater_hall ON theater_hall.id = movie_show_time.theater_hall_id AND theater_hall.deleted = 0 LEFT JOIN cinema_spec cs2 ON theater_hall.cinema_spec_id = cs2.id AND cs2.deleted = 0
movie_show_time.deleted = 0
AND movie.id = #{query.movieId}
AND cinema.id = #{query.cinemaId}
AND theater_hall.id = #{query.theaterHallId}
AND TO_DATE(movie_show_time.start_time, 'YYYY-MM-DD') = TO_DATE(#{query.date}, 'YYYY-MM-DD')
ORDER BY movie_show_time.update_time DESC
